import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Loader from '../components/Loader';
import { PokemonDetail as PokemonDetailType } from '../types';

import styles from '../styles/PokemonDetail.module.css';

interface PokemonDetailProps {
    pokemon: PokemonDetailType;
}

const PokemonDetail: React.FC<PokemonDetailProps> = ({ pokemon }) => {
    return (
        <div className={styles.container}>
            <img className={styles['pokemon-img']} src={pokemon.sprites.front_default} alt={pokemon.name} />
            <div className={styles.name}>{pokemon.name}</div>

            <div className={styles.section}>Types</div>
            <ul className={styles.list}>
                {pokemon.types.map((type) => (
                    <li key={type.type.name}>{type.type.name}</li>
                ))}
            </ul>

            <div className={styles.section}>Abilities</div>
            <ul className={styles.list}>
                {pokemon.abilities.map((ability) => (
                    <li key={ability.ability.name}>{ability.ability.name}</li>
                ))}
            </ul>

            <div className={styles.section}>Stats</div>
            <ul className={styles['stats-list']}>
                {pokemon.stats.map((stat) => (
                    <li key={stat.stat.name}>
                        <strong>{stat.stat.name}:</strong> {stat.base_stat}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default PokemonDetail;
