const typeColors: { [key: string]: string } = {
    fire: '#F08030',
    water: '#6890F0',
    grass: '#78C850',
    electric: '#F8D030',
    psychic: '#F85888',
    ice: '#98D8D8',
    dragon: '#7038F8',
    dark: '#705848',
    fairy: '#EE99AC',
    normal: '#A8A878',
    fighting: '#C03028',
    flying: '#A890F0',
    poison: '#A040A0',
    ground: '#E0C068',
    rock: '#B8A038',
    bug: '#A8B820',
    ghost: '#705898',
    steel: '#B8B8D0'
};

interface PokemonCardProps {
    pokemon: Pokemon;
    onClick?: () => void;
}

const PokemonCard: React.FC<PokemonCardProps> = ({ pokemon }) => {
    const { name, sprites, types } = pokemon;
    const navigate = useNavigate();

    const handleClick = () => {
        navigate(`/pokemon/${name}`);
    };

    // Pobierz pierwszy typ Pokemona
    const mainType = types && types.length > 0 ? types[0].type.name : 'normal';
    const bgColor = typeColors[mainType] || '#A8A878';

    return (
        <div
            className="pokemon-card"
            onClick={handleClick}
            style={{
                cursor: 'pointer',
                backgroundColor: bgColor,
                color: '#fff',
                borderRadius: '8px',
                padding: '10px',
                textAlign: 'center',
                transition: 'transform 0.2s'
            }}
        >
            <img src={sprites.front_default} alt={name} />
            <h3>{name}</h3>
            <div>{mainType.toUpperCase()}</div>
        </div>
    );
};

export default PokemonCard;
