import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Loader from '../components/Loader';
import { PokemonDetail as PokemonDetailType } from '../types';

import styles from '../styles/PokemonDetail.module.css';

interface PokemonDetailProps {
    pokemon: PokemonDetailType;
}

const PokemonDetail: React.FC<PokemonDetailProps> = ({ pokemon }) => {
    const typeColors: { [key: string]: string } = {
        fire: '#F08030',
        water: '#6890F0',
        grass: '#78C850',
        electric: '#F8D030',
        psychic: '#F85888',
        ice: '#98D8D8',
        dragon: '#7038F8',
        dark: '#705848',
        fairy: '#EE99AC',
        normal: '#A8A878',
        fighting: '#C03028',
        flying: '#A890F0',
        poison: '#A040A0',
        ground: '#E0C068',
        rock: '#B8A038',
        bug: '#A8B820',
        ghost: '#705898',
        steel: '#B8B8D0'
    };

    const mainType = pokemon.types && pokemon.types.length > 0 ? pokemon.types[0].type.name : 'normal';
    const bgColor = typeColors[mainType] || '#A8A878';

    return (
        <div
            className={styles.container}
            style={{
                background: `linear-gradient(135deg, ${bgColor} 60%, #fff 100%)`,
                boxShadow: '0 4px 24px rgba(0,0,0,0.12)',
                borderRadius: 16,
            }}
        >
            <img
                className={styles['pokemon-img']}
                src={pokemon.sprites.front_default}
                alt={pokemon.name}
                style={{
                    border: `2px solid ${bgColor}`,
                    background: '#f6f8fc',
                    borderRadius: '50%',
                    boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
                    width: '160px',
                    height: '160px',
                    objectFit: 'contain',
                }}
            />
            <div className={styles.name}>{pokemon.name}</div>

            <div className={styles.section}>Types</div>
            <div style={{ display: 'flex', justifyContent: 'center', gap: 10, marginBottom: 12 }}>
                {pokemon.types.map((type) => (
                    <span
                        key={type.type.name}
                        style={{
                            background: typeColors[type.type.name] || '#A8A878',
                            color: '#fff',
                            borderRadius: 12,
                            padding: '4px 16px',
                            fontSize: '1rem',
                            fontWeight: 500,
                            boxShadow: '0 1px 4px rgba(0,0,0,0.07)',
                            textTransform: 'capitalize',
                        }}
                    >
                        {type.type.name}
                    </span>
                ))}
            </div>

            <div className={styles.section}>Abilities</div>
            <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: 10, marginBottom: 12 }}>
                {pokemon.abilities.map((ability) => (
                    <span
                        key={ability.ability.name}
                        style={{
                            background: '#e0e7ff',
                            color: '#222',
                            borderRadius: 12,
                            padding: '4px 16px',
                            fontSize: '1rem',
                            fontWeight: 500,
                            boxShadow: '0 1px 4px rgba(0,0,0,0.07)',
                            textTransform: 'capitalize',
                        }}
                    >
                        {ability.ability.name}
                    </span>
                ))}
            </div>

            <div className={styles.section}>Stats</div>
            <ul className={styles['stats-list']}>
                {pokemon.stats.map((stat) => (
                    <li key={stat.stat.name}>
                        <strong>{stat.stat.name}:</strong> {stat.base_stat}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default PokemonDetail;
